package app.groceryapp;import android.app.Application;import com.bumptech.glide.Glide;import javax.inject.Inject;import app.groceryapp.data.DataManager;import app.groceryapp.utils.AppLogger;import dagger.android.AndroidInjector;import dagger.android.DispatchingAndroidInjector;import app.groceryapp.di.components.DaggerAppComponent;import dagger.android.HasAndroidInjector;public class GroceryApp extends Application implements HasAndroidInjector {  @Inject  DispatchingAndroidInjector<Object> activityDispatchingAndroidInjector;  @Inject  DataManager dataManager;  private static GroceryApp mApplicationInstance = null;  public static synchronized GroceryApp getInstance() {    return mApplicationInstance;  }  @Override  public void onCreate() {    super.onCreate();    mApplicationInstance = this;    DaggerAppComponent.builder()        .application(this)        .build()        .inject(this);    AppLogger.init();  }  @Override  public AndroidInjector<Object> androidInjector() {    return activityDispatchingAndroidInjector;  }  public DataManager getDataManager() {    return dataManager;  }  @Override  public void onLowMemory() {    super.onLowMemory();    try {      Glide.get(this).clearMemory();      new Thread(() -> Glide.get(getApplicationContext()).clearDiskCache()).start();    } catch (Exception e) {      AppLogger.d("onLowMemory %s", e.getLocalizedMessage());    }  }}