package app.groceryapp.enums;import app.groceryapp.R;public enum FontTypeEnum {    REGULAR_TYPE(0, R.font.nunito),    THIN_TYPE(1, R.font.nunito_light),    MEDIUM_TYPE(2, R.font.nunito_light),    BOLD_TYPE(3, R.font.nunito_bold),    SEMI_BOLD_TYPE(4, R.font.nunito_semibold),    EXTRA_BOLD_TYPE(5, R.font.nunito_extrabold),    BLACK_TYPE(6, R.font.nunito_black);        private int font_type;    private int font_id;    FontTypeEnum(int font_type, int font_id) {        this.font_type = font_type;        this.font_id = font_id;    }    public int getFontType() {        return font_type;    }    public int getFontId() {        return font_id;    }    public static FontTypeEnum getValue(int value) {        for(FontTypeEnum e: FontTypeEnum.values()) {            if(e.getFontType() == value) {                return e;            }        }        return FontTypeEnum.REGULAR_TYPE;    }}