package app.groceryapp.enums;public enum FontTypeEnum {    REGULAR_TYPE(0, "sans-serif"),    THIN_TYPE(1, "sans-serif-thin"),    MEDIUM_TYPE(2, "sans-serif-medium"),    BOLD_TYPE(3, "sans-serif-black");    private int font_type;    private String font_name;    FontTypeEnum(int font_type, String font_name) {        this.font_type = font_type;        this.font_name = font_name;    }    public int getFontType() {        return font_type;    }    public String getFontName() {        return font_name;    }    public static FontTypeEnum getValue(int value) {        for(FontTypeEnum e: FontTypeEnum.values()) {            if(e.getFontType() == value) {                return e;            }        }        return FontTypeEnum.REGULAR_TYPE;    }}