package app.groceryapp.enums;public enum HomeMenuEnum {  MANAGE_ACCOUNT(0, "One"),  TRANSACTION(1, "Two"),  RECURRING(2, "Three"),  VOLUNTEER(3, "Four");  private int menu_type;  private String menu_name;  HomeMenuEnum(int menu_type, String menu_name) {    this.menu_type = menu_type;    this.menu_name = menu_name;  }  public int getMenuType() {    return menu_type;  }  public String getMenuName() {    return menu_name;  }}