package app.groceryapp.ui.activity.main;import app.groceryapp.data.DataManager;import app.groceryapp.ui.base.BaseViewModel;import app.groceryapp.utils.constants.ApiConstants;import app.groceryapp.utils.rx.SchedulerProvider;public class MainActivityViewModel extends BaseViewModel<iMainActivityContract.iMainActivityNavigator> implements iMainActivityContract.iMainActivityViewModel {    public MainActivityViewModel(DataManager dataManager, SchedulerProvider schedulerProvider) {        super(dataManager, schedulerProvider);    }    @Override    public void loadCategory() {        if(isNetWorkConnected()) {            showLoader(true);            getDisposable().add(getDataManager()                .getCategory(ApiConstants.getBaseParams())                .subscribeOn(getSchedulerProvider().io())                .observeOn(getSchedulerProvider().ui())                .subscribe(dataResponse -> {                    showLoader(false);                    if (isError(dataResponse)) {                        getNavigator().showErrorLayout("", dataResponse.getErrorMessage());                    } else {                        getNavigator().setCategoryData(dataResponse.getCategoryList());                    }                }, throwable -> {                    showLoader(false);                    getNavigator().showErrorLayout("", throwable.getLocalizedMessage());                }));        }    }}