package app.groceryapp.ui.activity.main.adapter;import android.view.LayoutInflater;import android.view.ViewGroup;import org.jetbrains.annotations.NotNull;import java.util.List;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import app.groceryapp.databinding.LayoutHomeSideMenuItemBinding;import app.groceryapp.enums.HomeMenuEnum;import app.groceryapp.ui.base.BaseViewHolder;public class HomeSideMenuAdapter extends RecyclerView.Adapter<BaseViewHolder> {  private List<HomeMenuEnum> menuList;  private HomeSideMenuAdapterListener listener;  public interface HomeSideMenuAdapterListener {    void onSideMenuListItemClick(HomeMenuEnum homeMenuEnum);  }  public HomeSideMenuAdapter(List<HomeMenuEnum> menuList, HomeSideMenuAdapterListener listener) {    this.menuList = menuList;    this.listener = listener;  }  @NonNull  @Override  public BaseViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {    LayoutHomeSideMenuItemBinding binding = LayoutHomeSideMenuItemBinding.inflate(LayoutInflater.from(parent.getContext()), parent, false);    return new HomeSideMenuViewHolder(binding);  }  @Override  public void onBindViewHolder(@NonNull BaseViewHolder holder, int position) {    holder.onBind(position);  }  @Override  public int getItemCount() {    return menuList.size();  }  public class HomeSideMenuViewHolder extends BaseViewHolder {    private LayoutHomeSideMenuItemBinding binding;    HomeSideMenuViewHolder(@NotNull LayoutHomeSideMenuItemBinding binding) {      super(binding.getRoot());      this.binding = binding;    }    @Override    public void onBind(int position) {      binding.tvMenu.setText(menuList.get(position).getMenuName());      binding.llMenu.setOnClickListener(view -> {        if (listener != null) {          listener.onSideMenuListItemClick(menuList.get(position));        }      });    }  }}