package app.groceryapp.ui.activity.splash;import app.groceryapp.ui.activity.main.MainActivity;import app.groceryapp.databinding.ActivitySplashBinding;import app.groceryapp.ui.base.BaseActivity;import android.os.Bundle;import android.os.Handler;public class SplashActivity extends BaseActivity<SplashActivityViewModel>    implements iSplashActivityContract.iSplashNavigator {    private ActivitySplashBinding binding;    private SplashActivityViewModel viewModel;    @Override    public void setViewBinding() {        binding = ActivitySplashBinding.inflate(getLayoutInflater());        setContentView(binding.getRoot());    }    @Override    public void setViewModel() {        viewModel = (SplashActivityViewModel) prepareViewModel(SplashActivityViewModel.class);    }    @Override    public void setNavigator() {        viewModel.setNavigator(this);    }    @Override    public void handleErrorLayout(String errorTitle, String errorMessage) {    }    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        openMainActivity();    }    private void openMainActivity() {        new Handler().postDelayed(() -> {            startActivityWithAnimation(MainActivity.getIntent(this));            finish();        }, 500);    }}