package app.groceryapp.ui.base;import android.view.View;import org.jetbrains.annotations.NotNull;import java.util.Locale;import app.groceryapp.R;import app.groceryapp.data.model.api.response.product.ProductQuantity;public abstract class BaseProductListViewHolder extends BaseViewHolder {    public BaseProductListViewHolder(View itemView) {        super(itemView);    }    public String getProductStrikedPrice(ProductQuantity productQuantity) {        if (productQuantity.getProductOfferPercent() > 0) {            return String.format(Locale.getDefault(), itemView.getContext().getString(R.string.text_product_price), productQuantity.getProductPrice());        }        return "";    }    public String getProductPrice(ProductQuantity productQuantity) {        return String.format(Locale.getDefault(), itemView.getContext().getString(R.string.text_product_price), calculateOfferPrice(productQuantity));    }    private double calculateOfferPrice(@NotNull ProductQuantity productQuantity) {        double productPrice = productQuantity.getProductPrice();        if (productQuantity.getProductOfferPercent() > 0) {            productPrice = (double)productPrice -  (double)(productPrice * (productQuantity.getProductOfferPercent() / 100.00));        }        return productPrice;    }    public String getProductOffer(ProductQuantity productQuantity) {        if (productQuantity.getProductOfferPercent() > 0) {            return String.format(Locale.getDefault(), itemView.getContext().getString(R.string.text_product_offer), productQuantity.getProductOfferPercent());        }        return "";    }    public String getProductQuantity(ProductQuantity productQuantity) {        String quantity = "";        if (productQuantity.getItemsMultiplier() > 1) {            quantity = productQuantity.getItemsMultiplier() + " X ";        }        return quantity + (productQuantity.getProductQuantity() + " " + productQuantity.getUnit().getUnitName());    }}