package app.groceryapp.ui.custom;import android.content.Context;import android.util.AttributeSet;import android.view.View;import android.widget.Button;import android.widget.LinearLayout;import android.widget.RelativeLayout;import android.widget.TextView;import app.groceryapp.R;public class GroceryRelativeLayout extends RelativeLayout {    public LinearLayout llError;    public TextView tvErrorTitle;    public TextView tvErrorInfo;    public Button btnRetry;    public GroceryRelativeLayout(Context context) {        super(context);        init();    }    public GroceryRelativeLayout(Context context, AttributeSet attrs) {        super(context, attrs);        init();    }    public GroceryRelativeLayout(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);        init();    }    public GroceryRelativeLayout(Context context, AttributeSet attrs, int defStyleAttr, int defStyleRes) {        super(context, attrs, defStyleAttr, defStyleRes);        init();    }    public void init() {        View rootView =  inflate(getContext(), R.layout.layout_custom_relativelayout, this);        llError = rootView.findViewById(R.id.ll_error);        tvErrorTitle = rootView.findViewById(R.id.tv_error_title);        tvErrorInfo = rootView.findViewById(R.id.tv_error_info);        btnRetry = rootView.findViewById(R.id.btn_retry);    }}