package app.groceryapp.ui.custom;import android.content.Context;import android.content.res.TypedArray;import android.graphics.Typeface;import android.util.AttributeSet;import androidx.appcompat.widget.AppCompatTextView;import app.groceryapp.R;import app.groceryapp.enums.FontTypeEnum;import static app.groceryapp.enums.FontTypeEnum.BOLD_TYPE;import static app.groceryapp.enums.FontTypeEnum.MEDIUM_TYPE;import static app.groceryapp.enums.FontTypeEnum.REGULAR_TYPE;import static app.groceryapp.enums.FontTypeEnum.THIN_TYPE;public class GroceryTextView extends AppCompatTextView {    public GroceryTextView(Context context) {        super(context);    }    public GroceryTextView(Context context, AttributeSet attrs) {        super(context, attrs);        setFontFamily(context, attrs);    }    public GroceryTextView(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);        setFontFamily(context, attrs);    }    public void setFontFamily(Context context, AttributeSet attrs) {        int setFontFamily;        TypedArray typedArray = context.getTheme().obtainStyledAttributes(            attrs, R.styleable.GroceryTextView, 0, 0);        try {            setFontFamily = typedArray.getInteger(R.styleable.GroceryTextView_custom_font, 0);            setTypeface(getTypeFace(setFontFamily));        } finally {            typedArray.recycle();        }    }    public Typeface getTypeFace(int type) {        Typeface typeface = null;        try {            switch (FontTypeEnum.getValue(type)) {                default:                case REGULAR_TYPE:                    typeface = Typeface.create(REGULAR_TYPE.getFontName(), Typeface.NORMAL);                    break;                case THIN_TYPE:                    typeface = Typeface.create(THIN_TYPE.getFontName(), Typeface.NORMAL);                    break;                case MEDIUM_TYPE:                    typeface = Typeface.create(MEDIUM_TYPE.getFontName(), Typeface.NORMAL);                    break;                case BOLD_TYPE:                    typeface = Typeface.create(BOLD_TYPE.getFontName(), Typeface.BOLD);                    break;            }        } catch (Exception e) {            e.printStackTrace();        }        return typeface;    }}