package app.groceryapp.ui.fragment.home.adapter;import android.view.LayoutInflater;import android.view.ViewGroup;import com.bumptech.glide.Glide;import java.util.List;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import app.groceryapp.R;import app.groceryapp.data.model.api.response.category.SubCategory;import app.groceryapp.databinding.LayoutSubCategoryItemBinding;import app.groceryapp.ui.base.BaseViewHolder;public class SubCategoryGridAdapter extends RecyclerView.Adapter<SubCategoryGridAdapter.SubCategoryViewHolder> {    private List<SubCategory> subCategoryList;    private SubCategoryGridAdapterListener listener;    public interface SubCategoryGridAdapterListener {        void onSubCategoryItemClick(SubCategory subCategory);    }    public SubCategoryGridAdapter(List<SubCategory> subCategoryList, SubCategoryGridAdapterListener listener) {        this.subCategoryList = subCategoryList;        this.listener = listener;    }    @NonNull    @Override    public SubCategoryViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        LayoutSubCategoryItemBinding subCategoryItemBinding = LayoutSubCategoryItemBinding.inflate(LayoutInflater.from(parent.getContext()), parent, false);        return new SubCategoryViewHolder(subCategoryItemBinding);    }    @Override    public void onBindViewHolder(@NonNull SubCategoryViewHolder holder, int position) {        holder.onBind(position);    }    @Override    public int getItemCount() {        return subCategoryList.size();    }    class SubCategoryViewHolder extends BaseViewHolder {        LayoutSubCategoryItemBinding subCategoryItemBinding;        public SubCategoryViewHolder(LayoutSubCategoryItemBinding subCategoryItemBinding) {            super(subCategoryItemBinding.getRoot());            this.subCategoryItemBinding = subCategoryItemBinding;        }        @Override        public void onBind(int position) {            SubCategory subCategory = subCategoryList.get(position);            subCategoryItemBinding.tvSubCategoryName.setText(subCategory.getSubCategoryName());            Glide.with(subCategoryItemBinding.getRoot().getContext()).                load(subCategory.getImageFullPath()).                animate(R.anim.fade_in).                into(subCategoryItemBinding.ivSubCategoryImage);            subCategoryItemBinding.rlContent.setOnClickListener(view -> {                if(listener != null) {                    listener.onSubCategoryItemClick(subCategory);                }            });        }    }}